mapper.sql:
select * from T_ACCOUNT_RULE



		order by RULE_ID


		order by ${pageVO.orderBy}

sql.where:
1=1

	 and RULE_ID = #{accountRule.ruleId,jdbcType=CHAR}


	 and ACCOUNT_TYPE = #{accountRule.accountType,jdbcType=CHAR}


	 and PAYER_SUBJECT_NO = #{accountRule.payerSubjectNo,jdbcType=VARCHAR}


	 and PAYER_FLAG = #{accountRule.payerFlag,jdbcType=CHAR}


	 and PAYEE_SUBJECT_NO = #{accountRule.payeeSubjectNo,jdbcType=VARCHAR}


	 and PAYEE_FLAG = #{accountRule.payeeFlag,jdbcType=CHAR}


	 and UPDATE_TIME = #{accountRule.updateTime,jdbcType=TIMESTAMP}


	 and REMARK = #{accountRule.remark,jdbcType=VARCHAR}
